import { Component } from "react";

const styles={
    bubbleAlert:{
        backgroundColor:'#E9725A',
        borderRadius:'15px',
        color:'#fff',
        padding:'2px 10px',
        fontSize:'0.9rem',
        width:'20px',
    }
}

class BubbleAlert extends Component{

    getNumber(){
        if(!n){return ''}
        return n > 9 ? '9+' : n;
    }

    render(){
        return(
            <span style={styles.bubbleAlert}>
                5
            </span>
        )
    }
}

export default BubbleAlert
